function TurbineStatusByLocation() {
  const [rawData, setRawData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    locations: [],
    statuses: [],
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/data/turbines');
        const data = await response.json();
        setRawData(data);
      } catch (error) {
        console.error('Error fetching turbine data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const processTurbineStatusByLocation = (turbines) => {
    try {
      const locationStatusCounts = turbines.reduce((acc, turbine) => {
        const location = turbine.location || 'Unknown Location';
        const status = turbine.status || 'Unknown';
        if (!acc[location]) {
          acc[location] = { Active: 0, Warning: 0, Offline: 0 };
        }
        if (status in acc[location]) {
          acc[location][status]++;
        }
        return acc;
      }, {});
      return Object.entries(locationStatusCounts).map(([location, statuses]) => ({
        location,
        ...statuses
      }));
    } catch (error) {
      console.error('Error processing turbine status data:', error);
      return [];
    }
  };

  const applyFilters = (data) => {
    let filteredData = [...data];
    if (filters.locations.length > 0) {
      filteredData = filteredData.filter(item => filters.locations.includes(item.location));
    }
    if (filters.statuses.length > 0) {
      filteredData = filteredData.filter(item => filters.statuses.includes(item.status));
    }
    return filteredData;
  };

  const resetFilters = () => {
    setFilters({ locations: [], statuses: [] });
  };

  const activeFilterCount = Object.values(filters).filter(f => f.length > 0).length;

  if (loading) return <Card><CardContent className="flex items-center justify-center h-96"><div className="text-foreground">Loading...</div></CardContent></Card>;

